const Spotify = {


    redirectURI: "http://localhost:3000/",
    clientID: "756819499fd4455b8ebb596835f33a82",

    accessToken: '',
    expirationTime: '',

    getAccessToken() {
        if (this.accessToken) {
            return this.accessToken;
        } else if (window.location.href.match(/access_token=([^&]*)/) &&
        window.location.href.match(/expires_in=([^&]*)/)) {
            this.accessToken = window.location.href.match(/access_token=([^&]*)/); 
            this.expirationTime = window.location.href.match(/expires_in=([^&]*)/);
            window.setTimeout(() => this.accessToken = '', this.expirationTime * 1000);
            window.history.pushState('Access Token', null, '/');
        } else {
            window.location = `https://accounts.spotify.com/authorize?client_id=${this.clientID}&response_type=token&scope=playlist-modify-public&redirect_uri=${this.redirectURI}`;
        } 
    },

    search(term) {
        return fetch(`https://api.spotify.com/v1/search?q=${term}&type=track,album,artist`,
            { headers: {Authorization: `Bearer ${this.accessToken}`} }
        ).then( response =>  { 
            console.log(response)
            return response.body.items
        }).then( JSONResponse => JSONResponse.map( track => { 
            return {id: track.id, name: track.name, artist: track.artists[0].name, album: track.album.name, uri: track.uri} 
        } ) )
    },

    savePlaylist(playlistName, trackURIs) {
        if (!playlistName || !trackURIs) {
            return;
        }
        let accessTokenVar = this.accessToken;
        let headersVar = { Authorization: `Bearer ${accessTokenVar}`, "Content-Type": "application/json" };
        let userID = "";

        userID = fetch("https://api.spotify.com/v1/me", {headers: headersVar}
            ).then( response => {return response.json().id} )

        let playlistID = fetch(`https://api.spotify.com/v1/users/${userID}/playlists`,
            { headers: headersVar, body: {name: playlistName}, method: "POST" }
         ).then(response => { return response.json().id })
        playlistID = fetch(`https://api.spotify.com/v1/playlists/${playlistID}/tracks`, 
            { headers: headersVar, body: { uris: trackURIs } }
        ).then(response => { return response.json().id })
    }
}


export { Spotify };
